import { useEffect, useState } from "react";
import { Link } from "react-router-dom";
import WatchCard from "../components/WatchCard";
import watches from "../data/watches";
import BannerSlider from "../components/BannerSlider";

const Home = () => {
  const [featuredWatches, setFeaturedWatches] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    setTimeout(() => {
      setFeaturedWatches(watches.slice(0, 3));
      setLoading(false);
    }, 500);
  }, []);

  return (
    <div>
      <section
        style={{
          background: "linear-gradient(135deg, #1a1a1a 0%, #2d2d2d 100%)",
          color: "white",
          padding: "5rem 0",
          textAlign: "center",
        }}
      >
        <div className="container">
          <BannerSlider />
          <h1
            style={{
              fontSize: "3.5rem",
              marginBottom: "1rem",
              fontWeight: "700",
            }}
          >


          </h1>{" "}





          <Link
            to="/products"
            className="btn btn-primary"
            style={{ fontSize: "1.1rem", padding: "1rem 2.5rem" }}
          >
            Explore Collection{" "}
          </Link>{" "}
        </div>{" "}
      </section>{" "}
      <section
        className="container"
        style={{ marginTop: "4rem", marginBottom: "3rem" }}
      >
        <h2
          style={{
            textAlign: "center",
            marginBottom: "2.5rem",
            fontSize: "2.5rem",
          }}
        >
          Trusted Brands{" "}
        </h2>{" "}
        <div
          style={{
            display: "flex",
            flexWrap: "wrap",
            justifyContent: "center",
            gap: "1.5rem",
            marginBottom: "3rem",
            buttcolor: "#333",
          }}
        >
          {" "}
          {[
            "ROLEX",
            "PATEK PHILIPPE",
            "AUDEMARS PIGUET",
            "OMEGA",
            "CARTIER",
          ].map((brand) => (
            <div
              key={brand}
              style={{
                padding: "1.25rem 2.5rem",
                border: "2px solid #f0f0f0",
                borderRadius: "8px",
                fontWeight: "700",
                fontSize: "1.05rem",
                letterSpacing: "1px",
                transition: "all 0.3s ease",
                cursor: "default",
              }}
            >
              {" "}
              {brand}{" "}
            </div>
          ))}{" "}
        </div>{" "}
      </section>{" "}
      <section className="container" style={{ marginBottom: "4rem" }}>
        <h2
          style={{
            textAlign: "center",
            marginBottom: "2.5rem",
            fontSize: "2.5rem",
          }}
        >
          Featured Timepieces{" "}
        </h2>{" "}
        {loading ? (
          <div className="spinner" />
        ) : (
          <div className="products-grid">
            {" "}
            {featuredWatches.map((watch) => (
              <WatchCard key={watch.id} watch={watch} />
            ))}{" "}
          </div>
        )}{" "}
        <div style={{ textAlign: "center", marginTop: "2.5rem" }}>
          <Link
            to="/products"
            className="btn btn-secondary"
            style={{ fontSize: "1.1rem", padding: "0.9rem 2rem" }}
          >
            View All Watches{" "}
          </Link>{" "}
        </div>{" "}
      </section>{" "}
      <section
        style={{
          background: "#1a1a1a",
          color: "white",
          padding: "4rem 0",
          marginTop: "3rem",
        }}
      >
        <div className="container">
          <h2
            style={{
              textAlign: "center",
              marginBottom: "3rem",
              fontSize: "2.5rem",
            }}
          >
            Why Choose Us{" "}
          </h2>{" "}
          <div
            style={{
              display: "grid",
              gridTemplateColumns: "repeat(auto-fit, minmax(250px, 1fr))",
              gap: "3rem",
            }}
          >
            <div style={{ textAlign: "center" }}>
              <div style={{ fontSize: "3rem", marginBottom: "1rem" }}> ‚úì </div>{" "}
              <h3
                style={{
                  color: "#d4af37",
                  marginBottom: "0.75rem",
                  fontSize: "1.4rem",
                }}
              >
                100 % Authentic{" "}
              </h3>{" "}
              <p style={{ color: "#ccc", lineHeight: "1.8" }}>
                Every watch is verified by our expert team to ensure complete
                authenticity{" "}
              </p>{" "}
            </div>{" "}
            <div style={{ textAlign: "center" }}>
              <div style={{ fontSize: "3rem", marginBottom: "1rem" }}> üõ°Ô∏è </div>{" "}
              <h3
                style={{
                  color: "#d4af37",
                  marginBottom: "0.75rem",
                  fontSize: "1.4rem",
                }}
              >
                2 - Year Warranty{" "}
              </h3>{" "}
              <p style={{ color: "#ccc", lineHeight: "1.8" }}>
                Comprehensive coverage on all timepieces for your peace of
                mind{" "}
              </p>{" "}
            </div>{" "}
            <div style={{ textAlign: "center" }}>
              <div style={{ fontSize: "3rem", marginBottom: "1rem" }}> üåç </div>{" "}
              <h3
                style={{
                  color: "#d4af37",
                  marginBottom: "0.75rem",
                  fontSize: "1.4rem",
                }}
              >
                Global Shipping{" "}
              </h3>{" "}
              <p style={{ color: "#ccc", lineHeight: "1.8" }}>
                Secure worldwide delivery with full insurance and tracking{" "}
              </p>{" "}
            </div>{" "}
            <div style={{ textAlign: "center" }}>
              <div style={{ fontSize: "3rem", marginBottom: "1rem" }}> üîÑ </div>{" "}
              <h3
                style={{
                  color: "#d4af37",
                  marginBottom: "0.75rem",
                  fontSize: "1.4rem",
                }}
              >
                Trade - In Service{" "}
              </h3>{" "}
              <p style={{ color: "#ccc", lineHeight: "1.8" }}>
                Upgrade your collection with our flexible trade - in
                program{" "}
              </p>{" "}
            </div>{" "}
          </div>{" "}
        </div>{" "}
      </section>{" "}
      <section className="container" style={{ padding: "4rem 0" }}>
        <div
          style={{ textAlign: "center", maxWidth: "700px", margin: "0 auto" }}
        >
          <h2 style={{ marginBottom: "1.5rem", fontSize: "2.5rem" }}>
            Ready to Find Your Perfect Timepiece ?
          </h2>{" "}
          <p
            style={{
              color: "#666",
              marginBottom: "2rem",
              fontSize: "1.1rem",
              lineHeight: "1.8",
            }}
          >
            Browse our exclusive collection or contact us for personalized
            assistance in finding the watch of your dreams.{" "}
          </p>{" "}
          <div
            style={{
              display: "flex",
              gap: "1rem",
              justifyContent: "center",
              flexWrap: "wrap",
            }}
          >
            <Link
              to="/products"
              className="btn btn-primary"
              style={{ fontSize: "1.05rem" }}
            >
              Browse Collection{" "}
            </Link>{" "}
            <Link
              to="/contact"
              className="btn btn-secondary"
              style={{ fontSize: "1.05rem" }}
            >
              Contact Us{" "}
            </Link>{" "}
          </div>{" "}
        </div>{" "}
      </section>{" "}
    </div>
  );
};

export default Home;
