import { useEffect, useState } from "react";
import "../styles/BannerSlider.css";

const BannerSlider = () => {
    const [currentSlide, setCurrentSlide] = useState(0);

    const slides = [
        {
            id: 1,
            image: "/first-bannerimage.png",
            title: "Premium Watches",
            subtitle: "Discover Luxury Timepieces",
        },
        {
            id: 2,
            image: "/second-bannerimage.jpg",
            title: "Exclusive Collection",
            subtitle: "Handpicked Rarities",
        },
        {
            id: 3,
            image: "/third-bannerimage.jpg",
            title: "Authentic Luxury",
            subtitle: "100% Verified & Certified",
        },
    ];

    useEffect(() => {
        const timer = setInterval(() => {
            setCurrentSlide((prev) => (prev + 1) % slides.length);
        }, 5000);
        return () => clearInterval(timer);
    }, []);

    const goToSlide = (index) => {
        setCurrentSlide(index);
    };

    const nextSlide = () => {
        setCurrentSlide((prev) => (prev + 1) % slides.length);
    };

    const prevSlide = () => {
        setCurrentSlide((prev) => (prev - 1 + slides.length) % slides.length);
    };

    return (
        <div className='banner-slider'>
            <div className='slider-container'>
                {slides.map((slide, index) => (
                    <div
                        key={slide.id}
                        className={`slider-slide ${index === currentSlide ? "active" : ""}`}
                        style={{ backgroundImage: `url(${slide.image})` }}
                    >
                        <div className='slide-overlay'>
                            <div className='slide-content'>
                                <h1>{slide.title}</h1>
                                <p>{slide.subtitle}</p>
                                <button className='slide-btn'>Shop Now</button>
                            </div>
                        </div>
                    </div>
                ))}
            </div>

            {/* Navigation Arrows */}
            <button className='slider-arrow prev' onClick={prevSlide}>
                &#10094;
            </button>
            <button className='slider-arrow next' onClick={nextSlide}>
                &#10095;
            </button>

            {/* Dot Indicators */}
            <div className='slider-dots'>
                {slides.map((_, index) => (
                    <button
                        key={index}
                        className={`dot ${index === currentSlide ? "active" : ""}`}
                        onClick={() => goToSlide(index)}
                    />
                ))}
            </div>
        </div>
    );
};

export default BannerSlider;
