import React, { useState } from "react";
import "./Contact.css";

const Contact = () => {
  const [form, setForm] = useState({
    name: "",
    email: "",
    subject: "",
    message: ""
  });

  const handleChange = (e) => {
    setForm({ ...form, [e.target.name]: e.target.value });
  };

  // ✅ UPDATED SUBMIT FUNCTION
  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      const response = await fetch("http://localhost:5000/api/contact", {
        method: "POST",
        headers: {
          "Content-Type": "application/json"
        },
        body: JSON.stringify(form)
      });

      const data = await response.json();

      if (response.ok) {
        alert("Message sent successfully ✅");
        setForm({
          name: "",
          email: "",
          subject: "",
          message: ""
        });
      } else {
        alert(data.message || "Something went wrong ❌");
      }

    } catch (error) {
      console.error(error);
      alert("Server error ❌");
    }
  };

  return (
    <div className="contact-page">
      <div className="container">

        <div className="contact-header">
          <h1 className="contact-title">Get in Touch</h1>
          <p className="contact-subtitle">
            Have questions about HK DEALERS? Need support with your luxury watch purchase?
            We're here to help you.
          </p>
        </div>

        <div className="contact-grid">

          {/* LEFT SIDE FORM */}
          <div className="contact-form-section">
            <div className="form-card">
              <h2 className="form-title">Send us a Message</h2>

              <form onSubmit={handleSubmit} className="contact-form">

                <input
                  type="text"
                  name="name"
                  value={form.name}
                  onChange={handleChange}
                  placeholder="Full Name"
                  required
                />

                <input
                  type="email"
                  name="email"
                  value={form.email}
                  onChange={handleChange}
                  placeholder="Email Address"
                  required
                />

                <input
                  type="text"
                  name="subject"
                  value={form.subject}
                  onChange={handleChange}
                  placeholder="Subject"
                  required
                />

                <textarea
                  name="message"
                  value={form.message}
                  onChange={handleChange}
                  placeholder="Your Message"
                  rows="5"
                  required
                />

                <button type="submit" className="submit-btn">
                  Send Message
                </button>

              </form>
            </div>
          </div>

        </div>

        {/* ✅ FIXED CTA BUTTON */}
        <div className="cta-section">
          <h2 className="cta-title">Schedule a Private Viewing</h2>
          <p className="cta-text">
            Book an appointment to view our exclusive collection.
          </p>

          {/* Now just button (not link to backend) */}
          <button className="cta-btn">
            Book Appointment
          </button>

        </div>

      </div>
    </div>
  );
};

export default Contact;
