const handleSubmit = (e) => {
  e.preventDefault();
  setLoading(true);

  const templateParams = {
    name: form.name,
    email: form.email,
    subject: form.subject,
    message: form.message
  };

  emailjs
    .send(
      "service_x2sy4ov",
      "template_2mtyx2p",
      templateParams,
      "1_0YGRViezqRkyJFD"
    )
    .then(() => {
      alert("✅ Message sent successfully!");

      setForm({
        name: "",
        email: "",
        subject: "",
        message: ""
      });

      setLoading(false);
    })
    .catch((error) => {
      console.error(error);
      alert("❌ Failed to send message");
      setLoading(false);
    });
};
