import React, { useContext, useState } from "react";
import { Link } from "react-router-dom";
import { CartContext } from "../context/CartContext";

const Cart = () => {
  const { cartItems, removeFromCart, clearCart } = useContext(CartContext);
  const [form, setForm] = useState({
    name: "",
    email: "",
    phone: "",
    message: ""
  });

  const totalPrice = cartItems.reduce((sum, item) => sum + item.price, 0);

  const handleChange = (e) => {
    setForm({ ...form, [e.target.name]: e.target.value });
  };

  const sendCartEmail = (e) => {
    e.preventDefault();

    const productList = cartItems
      .map(
        (item, index) =>
          `${index + 1}. ${item.brand} ${item.model} - HKD ${item.price.toLocaleString()} (${item.condition})`
      )
      .join("\n");

    const emailSubject = `Cart Inquiry - ${cartItems.length} Watch${cartItems.length > 1 ? "es" : ""}`;
    const emailBody = `
Name: ${form.name}
Email: ${form.email}
Phone: ${form.phone}

I am interested in the following watches:

${productList}

Total Value: HKD ${totalPrice.toLocaleString()}

Message:
${form.message}
    `.trim();

    window.location.href = `mailto:your@email.com?subject=${encodeURIComponent(emailSubject)}&body=${encodeURIComponent(emailBody)}`;
  };

  if (cartItems.length === 0) {
    return (
      <div
        className="container"
        style={{
          paddingTop: "3rem",
          paddingBottom: "3rem",
          textAlign: "center"
        }}
      >
        <h2 style={{ marginBottom: "1rem" }}> Your Cart is Empty </h2>{" "}
        <p style={{ color: "#666", marginBottom: "2rem" }}>
          Looks like you haven 't added any watches to your cart yet.{" "}
        </p>{" "}
        <Link to="/products" className="btn btn-primary">
          Browse Watches{" "}
        </Link>{" "}
      </div>
    );
  }

  return (
    <div
      className="container"
