import React, { useContext, useState } from "react";
import { useParams } from "react-router-dom";
import watches from "../data/watches";
import { CartContext } from "../context/CartContext";

const ProductDetail = () => {
    const { id } = useParams();
    const product = watches.find((w) => w.id === Number(id));
    const { addToCart } = useContext(CartContext);

    const [form, setForm] = useState({
        name: "",
        email: "",
        message: "",
    });

    if (!product) return <div > Product not found < /div>;

    const handleChange = (e) => {
        setForm({...form, [e.target.name]: e.target.value });
    };

    const handleSubmit = (e) => {
        e.preventDefault();

        window.location.href = `mailto:your@email.com?subject=Inquiry about ${product.model}&body=
    Name: ${form.name}
    Email: ${form.email}
    Product: ${product.brand} ${product.model}
    Message: ${form.message}
    `;
    };

    return ( <
        div className = 'container' >
        <
        img src = { product.image }
        alt = { product.model }
        width = '300' /
        >
        <
        h2 > { " " } { product.brand } { product.model } { " " } <
        /h2>{" "} <
        p > { product.description } < /p>{" "} <
        h3 > HKD { product.price.toLocaleString() } < /h3> <
        button onClick = {
            () => addToCart(product) } > Add to Cart < /button> <
        hr / >
        <
        h3 > Send Inquiry < /h3>{" "} <
        form onSubmit = { handleSubmit } >
        <
        input name = 'name'
        placeholder = 'Your Name'
        onChange = { handleChange }
        required /
        >
        <
        input name = 'email'
        placeholder = 'Your Email'
        onChange = { handleChange }
        required /
        >
        <
        textarea name = 'message'
        placeholder = 'Your Message'
        onChange = { handleChange }
        />{" "} <
        button type = 'submit' > Send Email < /button>{" "} <
        /form>{" "} <
        /div>
    );
};

export default ProductDetail;
