import React, { useContext } from "react";
import { CartContext } from "../context/CartContext";

const Cart = () => {
  const { cartItems, removeFromCart } = useContext(CartContext);

  const sendCartEmail = () => {
    const productList = cartItems
      .map((item) => `${item.brand} ${item.model} - HKD ${item.price}`)
      .join("\n");

    window.location.href = `mailto:your@email.com?subject=Cart Order Inquiry&body=
    I am interested in the following watches:

    ${productList}
    `;
  };

  if (!cartItems.length) return <div> Your cart is empty. </div>;

  return (
    <div className="container">
      <h2> Your Cart </h2>
      {cartItems.map((item) => (
        <div key={item.id}>
          {" "}
          {item.brand} {item.model} - HKD {item.price}{" "}
          <button onClick={() => removeFromCart(item.id)}> Remove </button>{" "}
        </div>
      ))}
      <button onClick={sendCartEmail}> Send Cart Inquiry </button>{" "}
    </div>
  );
};

export default Cart;
