import React, { useContext, useState } from "react";
import { useParams, Link } from "react-router-dom";
import watches from "../data/watches";
import { CartContext } from "../context/CartContext";

const ProductDetail = () => {
  const { id } = useParams();
  const product = watches.find((w) => w.id === Number(id));
  const { addToCart } = useContext(CartContext);

  const [form, setForm] = useState({
    name: "",
    email: "",
    phone: "",
    message: ""
  });

  if (!product) {
    return (
      <div className="container" style={{ paddingTop: "3rem", textAlign: "center" }}>
        <h2>Product not found</h2>
        <Link to="/products" className="btn btn-primary" style={{ marginTop: "1rem" }}>
          Back to Products
        </Link>
      </div>
    );
  }

  const handleChange = (e) => {
    setForm({ ...form, [e.target.name]: e.target.value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();

    const emailSubject = `Product Inquiry - ${product.brand} ${product.model}`;
    const emailBody = `
CUSTOMER INFORMATION:
Name: ${form.name}
Email: ${form.email}
Phone: ${form.phone}

PRODUCT INQUIRY:
Brand: ${product.brand}
Model: ${product.model}
Price: HKD ${product.price.toLocaleString()}
Condition: ${product.condition}

CUSTOMER MESSAGE:
${form.message || "No additional message"}

---
This is a single product inquiry from WatchHK website.
    `.trim();

    window.location.href = `mailto:admin@watchhk.com?subject=${encodeURIComponent(emailSubject)}&body=${encodeURIComponent(emailBody)}`;

    // Reset form after submission
    setForm({ name: "", email: "", phone: "", message: "" });
    alert("Email client opened! Please send the email to complete your inquiry.");
  };

  return (
    <div className="container" style={{ paddingTop: "2rem", paddingBottom: "3rem" }}>
      <Link to="/products" style={{ display: "inline-block", marginBottom: "1.5rem", color: "#d4af37" }}>
        ← Back to Products
      </Link>

      <div style={{ display: "grid", gridTemplateColumns: "1fr 1fr", gap: "3rem", marginBottom: "3rem" }}>
        <div>
          <img
            src={product.image}
            alt={`${product.brand} ${product.model}`}
            style={{
              width: "100%",
              height: "auto",
              borderRadius: "8px",
              boxShadow: "0 4px 12px rgba(0,0,0,0.1)"
            }}
          />
        </div>

        <div>
          <div style={{ marginBottom: "0.5rem", color: "#666", fontSize: "0.9rem", textTransform: "uppercase" }}>
            {product.brand}
          </div>
          <h1 style={{ marginBottom: "1rem", fontSize: "2.5rem" }}>{product.model}</h1>

          <div style={{ 
            fontSize: "2rem", 
            fontWeight: "bold", 
            color: "#d4af37",
            marginBottom: "1rem" 
          }}>
            HKD {product.price.toLocaleString()}
          </div>

          <div style={{
            display: "inline-block",
            padding: "0.5rem 1rem",
            background: product.condition === "New" ? "#e8f5e9" : "#fff3e0",
            color: product.condition === "New" ? "#2e7d32" : "#e65100",
            borderRadius: "4px",
            fontSize: "0.9rem",
            fontWeight: "600",
            marginBottom: "2rem"
          }}>
            {product.condition}
          </div>

          <p style={{ 
            lineHeight: "1.8", 
            color: "#555",
            marginBottom: "2rem",
            fontSize: "1.05rem"
          }}>
            {product.description}
          </p>

          <button
            onClick={() => addToCart(product)}
            className="btn btn-primary"
            style={{
              width: "100%",
              padding: "1rem",
              fontSize: "1.1rem",
              marginBottom: "0.5rem"
            }}
          >
            Add to Cart
          </button>

          <div
            style={{
              textAlign: "center",
              padding: "0.75rem",
              background: "#f9f9f9",
              borderRadius: "4px",
              marginBottom: "2rem",
              fontSize: "0.9rem",
              color: "#666"
            }}
          >
            <strong>Two Ways to Purchase:</strong><br />
            1. Add to Cart → Buy multiple watches together<br />
            2. Fill inquiry form below → Buy this watch only
          </div>

          <div style={{
            background: "#f5f5f5",
            padding: "1.5rem",
            borderRadius: "8px",
            marginTop: "2rem"
          }}>
            <h3 style={{ marginBottom: "1rem", fontSize: "1.1rem" }}>Product Features</h3>
            <ul style={{ lineHeight: "2", color: "#555" }}>
              <li>100% Authentic Guarantee</li>
              <li>2-Year Warranty Included</li>
              <li>Free Worldwide Shipping</li>
              <li>30-Day Return Policy</li>
              <li>Certificate of Authenticity</li>
            </ul>
          </div>
        </div>
      </div>

      <div style={{
        background: "white",
        padding: "2.5rem",
        borderRadius: "8px",
        boxShadow: "0 2px 8px rgba(0,0,0,0.1)",
        maxWidth: "800px",
        margin: "0 auto"
      }}>
        <h2 style={{ marginBottom: "0.5rem", textAlign: "center" }}>Send Product Inquiry</h2>
        <p style={{ textAlign: "center", color: "#666", marginBottom: "2rem" }}>
          Interested in this timepiece? Send us a message and we'll get back to you shortly.
        </p>

        <form onSubmit={handleSubmit}>
          <div style={{ marginBottom: "1.5rem" }}>
            <label style={{ display: "block", marginBottom: "0.5rem", fontWeight: "600" }}>
              Full Name *
            </label>
            <input
              type="text"
              name="name"
              value={form.name}
              onChange={handleChange}
              placeholder="Enter your full name"
              required
              style={{
                width: "100%",
                padding: "0.75rem",
                border: "1px solid #ddd",
                borderRadius: "4px",
                fontSize: "1rem"
              }}
            />
          </div>

          <div style={{ marginBottom: "1.5rem" }}>
            <label style={{ display: "block", marginBottom: "0.5rem", fontWeight: "600" }}>
              Email Address *
            </label>
            <input
              type="email"
              name="email"
              value={form.email}
              onChange={handleChange}
              placeholder="your.email@example.com"
              required
              style={{
                width: "100%",
                padding: "0.75rem",
                border: "1px solid #ddd",
                borderRadius: "4px",
                fontSize: "1rem"
              }}
            />
          </div>

          <div style={{ marginBottom: "1.5rem" }}>
            <label style={{ display: "block", marginBottom: "0.5rem", fontWeight: "600" }}>
              Phone Number
            </label>
            <input
              type="tel"
              name="phone"
              value={form.phone}
              onChange={handleChange}
              placeholder="+852 1234 5678"
              style={{
                width: "100%",
                padding: "0.75rem",
                border: "1px solid #ddd",
                borderRadius: "4px",
                fontSize: "1rem"
              }}
            />
          </div>

          <div style={{ marginBottom: "1.5rem" }}>
            <label style={{ display: "block", marginBottom: "0.5rem", fontWeight: "600" }}>
              Message
            </label>
            <textarea
              name="message"
              value={form.message}
              onChange={handleChange}
              placeholder="Tell us more about your interest in this watch..."
              rows="5"
              style={{
                width: "100%",
                padding: "0.75rem",
                border: "1px solid #ddd",
                borderRadius: "4px",
                fontSize: "1rem",
                resize: "vertical"
              }}
            />
          </div>

          <div style={{
            background: "#f9f9f9",
            padding: "1rem",
            borderRadius: "4px",
            marginBottom: "1.5rem",
            fontSize: "0.9rem",
            color: "#666"
          }}>
            <strong>Inquiry for:</strong> {product.brand} {product.model} - HKD {product.price.toLocaleString()}
          </div>

          <button
            type="submit"
            className="btn btn-primary"
            style={{
              width: "100%",
              padding: "1rem",
              fontSize: "1.1rem"
            }}
          >
            Send Inquiry via Email
          </button>

          <p style={{ textAlign: "center", marginTop: "1rem", fontSize: "0.85rem", color: "#999" }}>
            * Required fields
          </p>
        </form>
      </div>
    </div>
  );
};

export default ProductDetail;
