import React from "react";
import "../app.css"; // Import CSS for styling

const About = () => {
  return (
    <div className='about-section'>
      <div className='about-container'>
        <div className='about-header'>
          <h1 className='about-title'> About Watch HK </h1>{" "}
          <p className='about-subtitle'>
            {" "}
            Excellence in Horology Since 2010{" "}
          </p>{" "}
        </div>{" "}
        <div className='about-hero'>
          <div className='hero-content'>
            <h2> Luxury Watches in HK Dealers </h2>{" "}
            <p>
              Discover authentic timepieces from the world 's finest
              watchmakers.Verified authenticity, expert service.We offer both
              new and pre - owned watches, each carefully authenticated by our
              expert horologists.{" "}
            </p>{" "}
          </div>{" "}
        </div>{" "}
        <div className='about-stats'>
          <div className='stat-item'>
            <span className='stat-number'> 13 + </span>{" "}
            <span className='stat-label'> Years Experience </span>{" "}
          </div>{" "}
          <div className='stat-item'>
            <span className='stat-number'> 2, 500 + </span>{" "}
            <span className='stat-label'> Watches Sold </span>{" "}
          </div>{" "}
          <div className='stat-item'>
            <span className='stat-number'> 100 % </span>{" "}
            <span className='stat-label'> Authenticity Guarantee </span>{" "}
          </div>{" "}
          <div className='stat-item'>
            <span className='stat-number'> 50 + </span>{" "}
            <span className='stat-label'> Brands Available </span>{" "}
          </div>{" "}
        </div>{" "}
        <div className='about-content-section'>
          <section className='about-content'>
            <h2 className='section-title'> Our Mission </h2>{" "}
            <p>
              Our mission is to provide watch enthusiasts with authentic, high -
              quality timepieces while delivering exceptional customer
              service.We believe in transparency, integrity, and building
              lasting relationships with our clients.{" "}
            </p>{" "}
          </section>{" "}
          <section className='about-content'>
            <h2 className='section-title'> Why Choose Watch HK ? </h2>{" "}
            <div className='features-grid'>
              <div className='feature-item'>
                <div className='feature-icon'> ‚úì </div>{" "}
                <h3> Expert Authentication </h3>{" "}
                <p>
                  Every watch undergoes rigorous authentication by certified
                  horologists{" "}
                </p>{" "}
              </div>{" "}
              <div className='feature-item'>
                <div className='feature-icon'> ‚úì </div>{" "}
                <h3> 2 - Year Warranty </h3>{" "}
                <p>
                  Comprehensive warranty on all timepieces for peace of
                  mind{" "}
                </p>{" "}
              </div>{" "}
              <div className='feature-item'>
                <div className='feature-icon'> ‚úì </div>{" "}
                <h3> Global Shipping </h3>{" "}
                <p>
                  Secure worldwide delivery with full insurance coverage{" "}
                </p>{" "}
              </div>{" "}
              <div className='feature-item'>
                <div className='feature-icon'> ‚úì </div>{" "}
                <h3> Buy Back Program </h3>{" "}
                <p>
                  Competitive buy - back guarantee for your timepieces{" "}
                </p>{" "}
              </div>{" "}
            </div>{" "}
          </section>{" "}
          <section className='about-content'>
            <h2 className='section-title'> Our Values </h2>{" "}
