import React, { useState, useEffect } from "react";
import "../styles/BannerSlider.css";
const BannerSlider = () => {
  const [currentSlide, setCurrentSlide] = useState(0);

  const slides = [
    {
      title: "Luxury Timepieces",
      subtitle: "Discover Prestigious Watch Brands",
      background: "linear-gradient(135deg, #1a1a1a 0%, #2d2d2d 100%)"
    },
    {
      title: "Authentic Watches",
      subtitle: "100% Verified & Certified",
      background: "linear-gradient(135deg, #2d2d2d 0%, #1a1a1a 100%)"
    },
    {
      title: "Expert Service",
      subtitle: "Professional Guidance & Support",
      background: "linear-gradient(135deg, #1a1a1a 0%, #3d3d3d 100%)"
    }
  ];

  useEffect(() => {
    const timer = setInterval(() => {
      setCurrentSlide((prev) => (prev + 1) % slides.length);
    }, 5000);

    return () => clearInterval(timer);
  }, [slides.length]);

  return (
    <div
      style={{
        position: "relative",
        height: "400px",
        overflow: "hidden"
      }}
    >
      {slides.map((slide, index) => (
        <div
          key={index}
          style={{
            position: "absolute",
            top: 0,
            left: 0,
            width: "100%",
            height: "100%",
            background: slide.background,
            display: "flex",
            alignItems: "center",
            justifyContent: "center",
            opacity: currentSlide === index ? 1 : 0,
            transition: "opacity 1s ease-in-out",
            color: "white",
            textAlign: "center"
          }}
        >
          <div>
            <h1 style={{ fontSize: "3rem", marginBottom: "1rem", fontWeight: "700" }}>
              {slide.title}
            </h1>
            <p style={{ fontSize: "1.5rem", color: "#d4af37" }}>
              {slide.subtitle}
            </p>
          </div>
        </div>
      ))}

      <div
        style={{
          position: "absolute",
          bottom: "20px",
          left: "50%",
          transform: "translateX(-50%)",
          display: "flex",
          gap: "10px"
        }}
      >
        {slides.map((_, index) => (
          <button
            key={index}
            onClick={() => setCurrentSlide(index)}
            style={{
              width: "12px",
              height: "12px",
              borderRadius: "50%",
              border: "2px solid white",
              background: currentSlide === index ? "white" : "transparent",
              cursor: "pointer",
              padding: 0
            }}
            aria-label={`Go to slide ${index + 1}`}
          />
        ))}
      </div>
    </div>
  );
};

export default BannerSlider;
